
import requests, json; func = lambda stop: exec("try:\n    req = requests.get('http://api.interurbanos.welbits.com/v1/stop/' + stop);((print('Quedan ' + json.loads(req.content.decode('utf8'))['lines'][0]['waitTime'].split(' ')[0] + ' minutos' if int(json.loads(req.content.decode('utf8'))['lines'][0]['waitTime'].split(' ')[0]) <= 10 else 'Quedan más de 10 minutos')) if len(json.loads(req.content.decode('utf8'))['lines']) > 0 else print('No hay buses')) if req.text.find('error') == -1 else print('No existe la parada')\nexcept:\n    print('No se pudo establecer conexión')"); func('20647')